INSERT INTO `gateways_detail` VALUES ('47', 'Anybus Communicator - Lonworks', '瑞典HMS工业网络有限公司', 'RS-232，RS-422,RS-485', 'LONWORKS', '工业普通级', '否', 'DC24 ±5V', '0 ~+55 °C  ', '该网关可实现转换Modbus寄存器数据到LonWorks变量(SNVT和/或UNVT)。您可以创建变量集合根据LonMark功能行规，或者您可以使用用户定义的网络变量(UNVT)创建您自己的变量集合。 \nCommunicator在Modbus RTU一侧是从站，这意味着您将其连接到已经有Modbus RTU主站连接的Modbus网络。 配置无需编程，是通过包含的Windows环境下的配置工具完成的。 ', '重量：150g \n尺寸：120 x 75 x 27mm ', null, '2020-06-30 14:23:26');
