INSERT INTO `gateways_detail` VALUES ('288', 'VT-M2M-LV', '成都万创科技有限责任公司', '以太网，RS232/485，USB，SD卡，GPIO', '2G/GPRS/3G/4G cellular, WLAN, GPS, Zigbee, Lora, 蓝牙', '工业普通级', '否', '宽电压输入 6 ~36V', '（-25）~ +70 ℃', 'VT-M2M-LV是一款专门针对物联网通讯及应用设计的，具有高可靠性，低功耗,低成本,高可拓展性等特点的ARM平台网关产品。\n', '重量：700g（带包装1100g)\n尺寸：146mm x 93.5mm x 45.5 mm、最大存储容量支持：NAND FLASH 256MB、 DDR156MHZ 128MB', null, '2020-06-30 14:23:26');
